mapper.select:
select t.bmid as id, t.bmid, t.bmbh, t.bmmc, t.sjbmid, (select bmmc from xtwh_bmxx where bmid = t.sjbmid) as sjbmmc, t.bmms, t.lrrq, t.lrrid, (select rymc from xtwh_ryxx where ryid = t.lrrid) as lrrmc, convert(length(t.bmid)/4-1,signed) as level, (case when (select count(1) from xtwh_bmxx where sjbmid = t.bmid)>0 then 'false' else 'true' end) as leaf, 'false' as expanded from xtwh_bmxx t
and t.sjbmid = 0
and t.sjbmid = #{nodeid}
and (t.bmbh like concat('%', #{s_bmmcdm}, '%') or t.bmmc like concat('%', #{s_bmmcdm}, '%'))
order by bmid asc limit ${start}, ${limit}

mapper.update:
update xtwh_bmxx
bmmc = #{bmmc, jdbcType=VARCHAR}, bmms = #{bmms, jdbcType=VARCHAR},
and bmid = #{bmid, jdbcType=VARCHAR}

mapper.select:
select count(0)
from xtwh_bmxx

    and sjbmid = #{sjbmid, jdbcType=VARCHAR}
