mapper.select:
select jsid, jsbh, jsmc, jsms, lrrq, lrrid, (select rymc from xtwh_ryxx where ryid = t.lrrid) as lrrmc from xtwh_jsxx t
and t.jsmc like concat('%', #{s_jsmc}, '%')
order by jsid asc limit ${start}, ${limit}

select count(0)
from xtwh_jsxx

    and jsmc = #{jsmc, jdbcType=VARCHAR}

        and jsid != #{jsid, jdbcType=INTEGER}

mapper.update:
update xtwh_jsxx
jsmc = #{jsmc, jdbcType=VARCHAR}, jsms = #{jsms, jdbcType=VARCHAR},
and jsid = #{jsid, jdbcType=INTEGER}

mapper.select:
select count(0)
from xtwh_ry_jsxx

    and jsid = #{jsid, jdbcType=INTEGER}
